import { api } from "../../api";
export const getPessoas = async(dispatch) => {
  const {data} = await api.get('/pessoa');
  if (data) {
    dispatch({
      type:'GET_PESSOAS',
      pessoas: data
    })
  } else {
    alert('Deu erro no login');
  }
}
