export const removeMaskCpf = (cpf) =>{
  return cpf.replaceAll(new RegExp(/[.-]/,'g'),'');
}

export const addMaskCpf = (cpf) => {
  return cpf.substr(0,3) + '.' + cpf.substr(3,3) + '.' + cpf.substr(6,3) + '-' + cpf.substr(9,2)
}
