import { api } from "../../api";
export const getPessoas = async(dispatch) => {
  const {data} = await api.get('/pessoa');
  if (data) {
    dispatch({
      type:'GET_PESSOAS',
      pessoas: data
    })
  } else {
    alert('Deu erro no login');
  }
}

export const prepareEdition = (pessoa)=>{
  console.log('pessoa é: ',pessoa)
}

export const deletePessoa = async (id) =>{
  const {data} = await api.delete(`/pessoa/${id}`);
  console.log("delete id é: ", id)
}
